// projects store
import Vue from "vue";
import {fetcheProjects} from "./views/projects/fetchedProjects.js";

export default {
  state: {
    projects: [],
    currentProject: null,

  },
  mutations: {
    SET_PROJECTS(state, projects) {
      state.projects = projects;
    },
    UPDATE_PROJECT(state, updatedProject) {
      const index = state.projects.findIndex(p => p._id === updatedProject._id)
      if (index !== -1) {
        Vue.set(state.projects, index, updatedProject)
      }
    },
    REMOVE_PROJECT(state, projectId) {
      state.projects = state.projects.filter(p => p._id !== projectId)
    }
  },
  actions: {
    async fetchProjects({ commit }) {
      try {
        const projects = await fetchedProjects()
        commit('SET_PROJECTS', projects)
      } catch (error) {
        commit('SET_ERROR', error.message)
      }
    },
  }
};
