import Vue from "vue";
import Vuex from "vuex";
import projects from "./views/projects/projects.js";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {},
  modules: {
    projects,
  },
  mutations: {
    SET_LOADING(state, { type, value }) {
      state.loading[type] = value
    },
  }
});
