// projects store
export default {
  state: {
    projects: [],
    currentProject: null,

  },
  mutations: {
    SET_PROJECTS(state, projects) {
      state.projects = projects;
    },
    SET_CURRENT_PROJECT(state, project) {
      state.currentProject = project
    },
  },
  actions: {
    async fetchProjects({ commit }) {
      commit("SET_CURRENT_PROJECT");
    }
  }
};
