// projects store
export default {
  state: {
    projects: [],
    currentProject: null,

  },
  mutations: {
    SET_PROJECTS(state, payload) {
      state.projects = payload;
    },
    SET_CURRENT_PROJECT(state, project) {
      state.currentProject = project
    },
  }
};
